<template>
  <div class="questionWrap" v-if="this.quizFinished == false" >
    <p id="question">
      {{ uiLabels.question }} {{ questionNumber }}: {{ question.q }}
    </p>     


     <!-- svarsalternativ         -->
    <div class="answerAlternatives" v-if="timer > -1 || question.timeOn == false">
        <button
          v-for="(a, index) in question.a"
          v-bind:class="{ selected: index === selectedAnswer }"
          v-on:click="
            changeColor(index);
            submitAnswer();"
          v-bind:key="a"
          class="isClicked catPawCursor">
          <span id="a"> {{ a }}</span>
        </button>
    </div>

    <div class ="timerFix">
      <div class="timer" v-if="question.timeOn == true && timer > -1" >
        {{ timer }} 
      </div>
    </div>

    <div id="timesUp" v-if="timer < 0 ">
      {{uiLabels.timesUp}}
    </div>
    
    <br />
    <!-- Frågor som submittas UTAN svar-->

    <div v-if="timer < 0 && question.timeOn == true" >
      <div v-if="lastQuestion">
        <button class="nextQuestionButton catPawCursor" v-on:click="emptyAnswer">
            {{ uiLabels.nextQuestion }}
        </button>
      </div>
      <div v-else class="result">
        <button
          class="finishQuizButton catPawCursor"
          v-on:click="
            finishQuizEmpty();
            confettin();">

          {{ uiLabels.finishQuiz }}
        </button>
      </div>
    
    </div>
    <div v-if="quizFinished" >
          <router-link id="routLink" v-bind:to="'/result/' + this.pollId">
            {{ uiLabels.seeResult }}
          </router-link>
    </div>
    
   <!-- Frågor som submittas med svar-->
    <div v-if="answerSubmitted">
      <div v-if="lastQuestion">
        <button class="nextQuestionButton catPawCursor" v-on:click="answer">
          {{ uiLabels.nextQuestion }}
        </button>
      </div>

      <div v-else class="result">
        <button
          class="finishQuizButton catPawCursor"
          v-on:click="
            finishQuiz();
            confettin();">

          {{ uiLabels.finishQuiz }}
        </button>

        <div v-if="quizFinished">
          <router-link id="routLink" v-bind:to="'/result/' + this.pollId">
            {{ uiLabels.seeResult }}
          </router-link>
        </div>
      </div>
    </div>

  </div>
<div v-if="quizFinished" >
  <div class ="popUpResult"> 
      Congratulations! You are done with the poll! 
  </div>

<div class="drawing-holder">
  <div class="cat-face">
          
		<div class="cat-eyes"></div>
    <div class="cat-nose"></div>
		<div class="cat-mouth"></div>    
	</div>
</div>

  <router-link id="routLink" v-bind:to="'/result/' + this.pollId">
    {{ uiLabels.seeResult }}
  </router-link>

  

</div>

</template>


<script>
export default {
  name: "Question",
  props: {
    question: Object,
    uiLabels: Object,
    amountQuestion: Number,
    method: { type: Function }, //HÄR kommer konfettin in från sin parent som en props mvh Elsa
  },

  data: function () {
    return {
      pollId: "inactive poll",
      questionNumber: 1,
      selectedAnswer: null, //index som anger vilket alternativ som är valt
      submittedAnswer: null, //index som anger vilket svarsalternativ som har skickats
      answerSubmitted: false, //boolean som anger om man har skickat ett svar
      lastQuestion: true,
      quizFinished: false,
      timerFunction: null,
      timer: 0,
    };
  },

  computed: {
    thisTimeOn: function () {
      return this.question.timeOn;
    },
  },

  watch: {
    question: {
      handler() {

        this.timer = this.question.time;
        clearInterval(this.timerFunction);
        if (this.thisTimeOn) {
          this.timerFunction = setInterval(() => {
            this.timer--;
          }, 1000);
          
        }
        
      },
      immediate: true,
    },
  },
  created: function () {
    this.pollId = this.$route.params.id;
  },


  methods: {
    emptyAnswer: function(){
      this.answerSubmitted = true;
      this.submittedAnswer = '';
      this.answer();
    },
    finishQuizEmpty: function(){
      this.answerSubmitted = true;
      this.submittedAnswer = '';
      this.finishQuiz();
      this.answerSubmitted = false;

    },

    submitAnswer: function () {
      this.submittedAnswer = this.question.a[this.selectedAnswer];
      console.log(this.submittedAnswer);
      this.answerSubmitted = true;
    },
    answer: function () {
      if (this.answerSubmitted == true && this.submittedAnswer != null) {
        this.$emit("answer", this.submittedAnswer);
        this.selectedAnswer = null;
        this.answerSubmitted = false;
        this.submittedAnswer = null;
      }
      console.log(this.questionNumber);
      console.log(this.amountQuestion);
      this.questionNumber = this.questionNumber + 1;

      if (this.questionNumber === this.amountQuestion) {
        this.lastQuestion = false; //Innebär att de är sista frågan!!! false är egentligen true :) /elsa
      }
    },

    finishQuiz: function () {
      if (this.answerSubmitted == true && this.submittedAnswer != null) {
        this.quizFinished = true;
        this.$emit("answer", this.submittedAnswer);
      }
    },

    changeColor: function (i) {
      if (this.selectedAnswer != i) {
        this.selectedAnswer = i;
      } else {
        this.selectedAnswer = null;
      }
    },
    startTimer: function (t) {
      if (t === "unlimited") {
        this.timerOn = false;
        this.timer = 100;
      } else {
        this.timer = parseInt(t);
      }
    },
    confettin: function () {
      this.method();
    },
  },
};
</script>
