<script>
import io from "socket.io-client";
const socket = io();

export default {
  name: "Create",
  data: function () {
    return {
      lang: "",
      pollId: "",
      answers: ["", ""],
      questionNumber: 1,
      uiLabels: {},
      pollHeadline: "",
      editActivated: false,
      polls: null,
      indexForChosenQuestion: 0,
      pollIdInput: "",
    };
  },
  computed: {
    question: {
      get: function () {
        if (
          this.polls &&
          this.pollId &&
          this.polls[this.pollId] &&
          this.polls[this.pollId].questions &&
          this.polls[this.pollId].questions[this.indexForChosenQuestion]
        ) {
          console.log(this.pollId);
          console.log(this.polls);
          console.log(this.polls[this.pollId]);
          return this.polls[this.pollId].questions[this.indexForChosenQuestion]
            .q;
        } else {
          return "";
        }
      },
      set: function (q) {
        this.polls[this.pollId].questions[this.indexForChosenQuestion].q = q;
      },
    },
    time: {
      get: function () {
        if (
          this.polls &&
          this.pollId &&
          this.polls[this.pollId] &&
          this.polls[this.pollId].questions &&
          this.polls[this.pollId].questions[this.indexForChosenQuestion]
        ) {
          return this.polls[this.pollId].questions[this.indexForChosenQuestion]
            .time;
        } else {
          return "";
        }
      },
      set: function (t) {
        this.polls[this.pollId].questions[this.indexForChosenQuestion].time = t;
      },
    },
  },
  created: function () {
    this.lang = this.$route.params.lang;
    socket.emit("pageLoaded", this.lang);
    socket.emit("getAllPolls");
    socket.on("init", (labels) => {
      this.uiLabels = labels;
    });

    socket.on("pollCreated", () => {
      this.pollId = this.newPollId;
      this.newPollId = "";
      this.addQuestion(0);
    });
    socket.on("pollHead", (pollHead) => (this.pollHeadline = pollHead));
    socket.on("emitAllPolls", (data) => {
      this.polls = data;
      this.bindVariables();
    });
  },
  methods: {
    bindVariables: function () {
      if (
        this.pollId &&
        this.polls[this.pollId].questions[this.indexForChosenQuestion]
      ) {
        this.answers =
          this.polls[this.pollId].questions[this.indexForChosenQuestion].a;
      }
    },
    saveEditedQuestion: function () {
      socket.emit("saveEditedQuestion", {
        pollId: this.pollId,
        q: this.question,
        a: this.answers,
        t: this.time,
      });
    },

    infoFunction: function () {
      var x = document.getElementById("infoDIV");
      if (x.style.display === "none") {
        x.style.display = "block";
      } else {
        x.style.display = "none";
      }
    },

    runPollFunction: function () {
      console.log(this.pollId);
      socket.emit("runPoll", this.pollId);
    },

    chooseQuestionWhenSelectingPoll: function (pollId, indexForChosenQuestion) {
      socket.emit("chooseQuestion", {
        pollId: pollId,
        indexForChosenQuestion: indexForChosenQuestion, //alltid =0 här
      });
      this.pollId = pollId;
      console.log(this.question, this.answers, "q and a 1"); //här blir något fel, krävs 3 tryck för att uppdatera question och answers
      this.indexForChosenQuestion = indexForChosenQuestion;
      console.log(this.question, this.answers, "q and a2");
      this.answers =
        this.polls[this.pollId].questions[indexForChosenQuestion].a;
    },

    chooseQuestion: function (indexForChosenQuestion) {
      socket.emit("chooseQuestion", {
        pollId: this.pollId,
        indexForChosenQuestion: indexForChosenQuestion,
      });
      this.indexForChosenQuestion = indexForChosenQuestion;
      this.answers =
        this.polls[this.pollId].questions[indexForChosenQuestion].a;
    },
    moveQuestion: function (direction) {
      console.log("moveQuestion fungerar", direction);
      socket.emit("moveQuestion", {
        pollId: this.pollId,
        direction: direction,
      });
      this.updatePolls();
      if (direction == "up") {
        this.indexForChosenQuestion -= 1;
      }
      if (direction == "down") {
        this.indexForChosenQuestion += 1;
      }
    },

    updatePolls: function () {
      socket.emit("getAllPolls");
    },

    deleteQuestion: function () {
      socket.emit("deleteQuestion", this.pollId);
      this.indexForChosenQuestion -= 1;
    },
    createPoll: function () {
      if (typeof this.polls[this.newPollId] === "undefined") {
        socket.emit("createPoll", { pollId: this.newPollId, lang: this.lang });
        socket.emit("getAllPolls");
        
      }
    },
    selectPoll: function (pollId) {
      socket.emit("createPoll", { pollId: pollId });
      socket.emit("getAllPolls");
      this.pollId = pollId;
    },
    deletePoll: function () {
      if (confirm(this.uiLabels.confirmDeletePoll)) {
        socket.emit("deletePoll", { pollId: this.pollId });
        this.pollHeadline = this.uiLabels.createHeader;
        this.pollId = "";
        socket.emit("getAllPolls");
      }
    },
    addQuestion: function (indexForAddedQuestion) {
      socket.emit("addQuestion", {
        pollId: this.pollId,
        q: this.uiLabels.editMe,
        a: ["", ""],
        t: "0",
        indexForAddedQuestion,
      });
      socket.emit("getAllPolls");
      this.indexForChosenQuestion = indexForAddedQuestion;
    },
    addAnswer: function () {
      this.answers.push("");
    },
    delAnswer: function (index) {
      this.answers.splice(index, 1);
    },
    runQuestion: function () {
      socket.emit("runQuestion", {
        pollId: this.pollId,
        questionNumber: this.questionNumber,
      });
    },
    editOrSavePoll: function (mode) {
      if (
        confirm(
          "uilabel saknas, are you sure you want to save? You cannot make any changes after this."
        )
      ) {
        socket.emit("editOrSavePoll", { mode: mode, pollId: this.pollId });
      }
    },
  },
};
</script>

