<script>
import io from "socket.io-client";
const socket = io();

export default {
  name: "Create",
  data: function () {
    return {
      lang: "",
      pollId: "",
      question: "",
      answers: ["", ""],
      questionNumber: 1,
      data: {},
      uiLabels: {},
      pollHeadline: "",
      time: "0",
      selectedAnswer: 0, //används bara för färgbyte på frågeknapparna
      editActivated: false,
      polls: [],
    };
  },
  created: function () {
    this.lang = this.$route.params.lang;
    socket.emit("pageLoaded", this.lang);
    socket.emit("getAllPolls");
    socket.on("init", (labels) => {
      this.uiLabels = labels;
    });
    socket.on("dataUpdate", (data) => (this.data = data));
    socket.on("pollCreated", (data) => (this.data = data));
    socket.on("allQuestions", (data) => (this.data = data));
    socket.on("pollHead", (pollHead) => (this.pollHeadline = pollHead));
    socket.on("getAllPolls", (data) => (this.polls = data));

    // socket.on("updateChooseQuestion", (data) => (this.data = data));
  },
  methods: {
    saveEditedQuestion: function () {
      socket.emit("saveEditedQuestion", {
        pollId: this.pollId,
        // q: this.question,
        q: this.question,
        // a: this.answers,
        a: this.answers,
        t: this.time,
      });
    },

    infoFunction: function () {
      var x = document.getElementById("infoDIV");
      if (x.style.display === "none") {
        x.style.display = "block";
      } else {
        x.style.display = "none";
      }
    },

    runPollFunction: function () {
      console.log(this.pollId);
      socket.emit("runPoll", {
        pollId: this.pollId,
      });
      //VARFÖR FUNKAR DE INTE
    },

    changeColor: function (i) {
      if (this.selectedAnswer != i) {
        this.selectedAnswer = i;
      }
    },

    chooseQuestionWhenSelectingPoll: function (pollId, indexForChosenQuestion) {
      socket.emit("chooseQuestion", {
        pollId: pollId,
        indexForChosenQuestion: indexForChosenQuestion,
      });
      this.question = this.data.poll.questions[indexForChosenQuestion].q;
      this.answers = this.data.poll.questions[indexForChosenQuestion].a;
      this.time = this.data.poll.questions[indexForChosenQuestion].t;
    },

    chooseQuestion: function (indexForChosenQuestion) {
      socket.emit("chooseQuestion", {
        pollId: this.pollId,
        indexForChosenQuestion: indexForChosenQuestion,
      });
      this.question = this.data.poll.questions[indexForChosenQuestion].q;
      this.answers = this.data.poll.questions[indexForChosenQuestion].a;
      this.time = this.data.poll.questions[indexForChosenQuestion].t;
    },
    moveQuestion: function (direction, editQuestion) {
      console.log("moveQuestion fungerar", direction);
      socket.emit("moveQuestion", {
        pollId: this.pollId,
        direction: direction,
      });
      if (direction == "up") {
        this.changeColor(editQuestion - 1);
      }
      if (direction == "down") {
        this.changeColor(editQuestion + 1);
      }
      // this.question = this.data.poll.questions[this.data.poll.editQuestion].q;
      // this.answers = this.data.poll.questions[this.data.poll.editQuestion].a;
    },

    deleteQuestion: function () {
      socket.emit("deleteQuestion", this.pollId);
      this.question =
        this.data.poll.questions[this.data.poll.questions.length - 1].q;
      this.answers =
        this.data.poll.questions[this.data.poll.questions.length - 1].a;
    },
    createPoll: function () {
      socket.emit("createPoll", { pollId: this.pollId, lang: this.lang });
      socket.emit("getAllPolls");
    },
    selectPoll: function (pollId) {
      socket.emit("createPoll", { pollId: pollId });
      socket.emit("getAllPolls");
      this.pollId = pollId;
    },
    deletePoll: function () {
      if (confirm(this.uiLabels.confirmDeletePoll)) {
        socket.emit("deletePoll", { pollId: this.pollId });
        this.pollHeadline = this.uiLabels.createHeader;
        this.pollId = "";
        socket.emit("getAllPolls");
      }
    },
    addQuestion: function (indexForAddedQuestion) {
      socket.emit("addQuestion", {
        pollId: this.pollId,
        // q: this.question,
        q: this.uiLabels.editMe,
        // a: this.answers,
        a: ["", ""],
        t: "0",
        indexForAddedQuestion,
      });
      this.question = this.uiLabels.editMe;
      this.answers = ["", ""];
      this.selectedAnswer = indexForAddedQuestion;
    },
    addAnswer: function () {
      this.answers.push("");
    },
    delAnswer: function (index) {
      this.answers.splice(index, 1);
    },
    runQuestion: function () {
      socket.emit("runQuestion", {
        pollId: this.pollId,
        questionNumber: this.questionNumber,
      });
      // console.log(typeof this.questionNumber, this.questionNumber); //ger number och siffran som står i fältet
    },
    editOrSavePoll: function (mode) {
      socket.emit("editOrSavePoll", { mode: mode, pollId: this.pollId });
    },
  },
};
</script>

