<template>
  <section class="catCursor">
    <header>
      <h1>{{ pollId }}</h1>
    </header>

    <p>
      {{ uiLabels.totalAmountofQuestions }} {{ thePoll.poll.questions.length }}
    </p>
    <div class="bars">
      <div
        class="answered"
        v-bind:style="{
          width: (100 / thePoll.poll.questions.length) * questionNumber + '%',
        }"
      ></div>
    </div>

    <main>
      <div>
        <Question
          v-bind:uiLabels="uiLabels"
          v-bind:question="question"
          v-bind:method="confettiButton"
          v-bind:amountQuestion="thePoll.poll.questions.length"
          v-on:answer="submitAnswer"
        />
      </div>
    </main>

    <!--
      {{ uiLabels.youareonQnumber }} {{ this.questionNumber + 1 }}
   

    <div v-for="index in thePoll.poll.questions.length" :key="index">
      <div class="numberOfQuestions" type="number" v-bind="index">
        {{ uiLabels.question }} {{ index }}
      </div>
    </div>
    -->

