</template>

<script>
import Bars from "@/components/Bars.vue";
import Pie from "@/components/Pie.vue";
import io from "socket.io-client";
const socket = io();

export default {
  name: "Result",
  components: {
    Bars,
    Pie,
  },
  data: function () {
    return {
      uiLabels: {},
      lang: "",
      question: "",
      data: {},
      questionNumber: 0,
      thePoll: {},
      pollId: "",
      colorArray: [
        "#f46036",
        "#1b998b",
        "#c5d86d",
        "#d7263d",
        "#bfebf8",
        "#fccc7a",
        "#f6b4c5",
        "#ff0000",
        "#D85D55",
      ],
      dataRepresentation: "Bars",
      selectedQuestion: 0,
    };
  },

  created: function () {
    console.log('result skapas');
    this.pollId = this.$route.params.id;
    socket.emit("joinPoll", {
      pollId: this.pollId,
      questionNumber: this.questionNumber,
    });
    socket.on("dataUpdate", (update) => {
      this.data = update.a;
      let keys = Object.keys(this.data);
      for (let i = 0; i < keys.length; i++) {
        this.data[keys[i]] = {
          count: this.data[keys[i]],
          color: this.colorArray[i],
        };
      }
      this.question = update.q;
    });
    socket.on("newQuestion", (update) => {
      this.question = update.q;
      this.data = {};
    });
    socket.emit("emitGetPoll", this.pollId);
    socket.on("getPoll", (thePoll) => {
      this.thePoll = thePoll;
      socket.emit("pageLoaded", this.thePoll.poll.lang);
    });
    socket.on("init", (labels) => {
      this.uiLabels = labels;
    });
  },
  methods: {
    selectQuestion: function (questionNumber) {
      socket.emit("joinPoll", { pollId: this.pollId, questionNumber });
    },
    switchComponent: function () {
      if (this.dataRepresentation === "Bars") {
        this.dataRepresentation = "Pie";
        document.getElementsByClassName("switchComponentBtn").innerHTML =
          this.uiLabels.showBars;
      } else {
        this.dataRepresentation = "Bars";
        document.getElementsByClassName("switchComponentBtn").innerHTML =
          this.uiLabels.showPie;
      }
    },
    changeColor: function (i) {
      if (this.selectedQuestion != i) {
        this.selectedQuestion = i;
      }
    },
  },
};
</script>

