<script>
import io from "socket.io-client";
import meow from "/public/sounds/meow.mp3";
const socket = io();

export default {
  name: "Start",
  data: function () {
    return {
      uiLabels: {},
      id: "",
      lang: "en",
    };
  },
  created: function () {
    socket.emit("pageLoaded", this.lang);
    socket.on("init", (labels) => {
      this.uiLabels = labels;
    });
  },
  methods: {
    playSound: function () {
      const audio = new Audio(meow);
      audio.play();
    },

    // switchLanguage: function () {
    //   if (this.lang === "en") this.lang = "sv";
    //   else this.lang = "en";
    //   socket.emit("switchLanguage", this.lang);
    // },
    infoFunction: function () {
      var x = document.getElementById("infoDIV");
      if (x.style.display === "none") {
        x.style.display = "block";
      } else {
        x.style.display = "none";
      }
    },
    switchLanguageToEN: function () {
      this.lang = "en";
      socket.emit("switchLanguage", this.lang);
    },
    switchLanguageToSV: function () {
      this.lang = "sv";
      socket.emit("switchLanguage", this.lang);
    },
    checkPollExist: function () {
      console.log("from participate: ", this.id, this.id.value)
      socket.emit("emitGetPoll", this.id);
      socket.on("getPoll", (idOfPoll) => {
        if (typeof idOfPoll !== "undefined") {
          alert(idOfPoll.value);
          
        }
      });
      
    },
  },
};
</script>

